// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IDisperse.sol";
import "./libraries/SwapUtils.sol";

contract Disperse is IDisperse {
    using SafeERC20 for IERC20;
    using Address for address payable;

    address public immutable sgProxy;
    mapping(address => mapping(address => uint256)) public balances;

    constructor(address _sgProxy) {
        sgProxy = _sgProxy;
    }

    receive() external payable {}

    function onReceiveERC20(
        address token,
        address to,
        uint256 amount
    ) external {
        if (msg.sender != sgProxy) revert InvalidProxy();
        if (token == address(0)) revert InvalidToken();

        balances[token][to] += amount;

        emit OnReceiveERC20(token, to, amount);
    }

    function sgProxyReceive(
        address srcFrom,
        address token,
        uint256 amount,
        bytes calldata data
    ) external {
        if (msg.sender != sgProxy) revert InvalidProxy();

        (
            address tokenOut,
            address swapTo,
            bytes memory swapData,
            address[] memory recipients,
            uint256[] memory amounts
        ) = abi.decode(data, (address, address, bytes, address[], uint256[]));

        _disperse(token, amount, tokenOut, swapTo, swapData, recipients, amounts, srcFrom);

        emit SgProxyReceive(srcFrom, token, amount, data);
    }

    function withdraw(
        address token,
        address to,
        uint256 amount
    ) external {
        if (amount > balances[token][msg.sender]) revert InsufficientBalance();
        balances[token][msg.sender] -= amount;

        IERC20(token).safeTransfer(to, amount);

        emit Withdraw(token, to, amount);
    }

    function disperse(DisperseParams calldata params) external {
        IERC20(params.tokenIn).safeTransferFrom(msg.sender, address(this), params.amountIn);

        _disperse(
            params.tokenIn,
            params.amountIn,
            params.tokenOut,
            params.swapTo,
            params.swapData,
            params.recipients,
            params.amounts,
            msg.sender
        );
    }

    function disperseIntrinsic(DisperseParams calldata params) external {
        if (params.amountIn > balances[params.tokenIn][msg.sender]) revert InsufficientBalance();
        balances[params.tokenIn][msg.sender] -= params.amountIn;

        _disperse(
            params.tokenIn,
            params.amountIn,
            params.tokenOut,
            params.swapTo,
            params.swapData,
            params.recipients,
            params.amounts,
            msg.sender
        );
    }

    function _disperse(
        address tokenIn,
        uint256 amountIn,
        address tokenOut,
        address swapTo,
        bytes memory swapData,
        address[] memory recipients,
        uint256[] memory amounts,
        address refundAddress
    ) internal {
        uint256 length = recipients.length;
        if (length != amounts.length) revert InvalidParams();

        if (swapTo != address(0)) {
            SwapUtils.swapERC20(tokenIn, amountIn, swapTo, swapData, tokenIn != tokenOut, refundAddress);
        }

        if (tokenOut == address(0)) {
            for (uint256 i; i < length; ) {
                uint256 amount = amounts[i];
                if (amount > 0) {
                    payable(recipients[i]).sendValue(amount);
                }
                unchecked {
                    ++i;
                }
            }
        } else {
            for (uint256 i; i < length; ) {
                uint256 amount = amounts[i];
                if (amount > 0) {
                    IERC20(tokenOut).safeTransfer(recipients[i], amount);
                }
                unchecked {
                    ++i;
                }
            }
            uint256 balanceTokenOut = IERC20(tokenOut).balanceOf(address(this));
            if (balanceTokenOut > 0) {
                IERC20(tokenOut).safeTransfer(refundAddress, balanceTokenOut);
            }
        }

        uint256 balance = address(this).balance;
        if (balance > 0) {
            payable(refundAddress).sendValue(balance);
        }
    }
}

contracts/interfaces/IDisperse.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "./IStargateProxyReceiver.sol";

interface IDisperse is IStargateProxyReceiver {
    error InvalidToken();
    error InsufficientBalance();
    error InvalidParams();
    error InvalidSwapData();

    event Disperse(address indexed token, address[] recipients, uint256[] amounts);
    event Withdraw(address indexed token, address indexed to, uint256 amount);

    struct DisperseParams {
        uint256 amountIn;
        address tokenIn;
        address tokenOut;
        address swapTo;
        bytes swapData;
        address[] recipients;
        uint256[] amounts;
        address refundAddress;
    }

    function withdraw(
        address token,
        address to,
        uint256 amount
    ) external;

    function disperse(DisperseParams calldata params) external;

    function disperseIntrinsic(DisperseParams calldata params) external;
}

contracts/libraries/SwapUtils.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

library SwapUtils {
    using SafeERC20 for IERC20;
    using Address for address payable;

    error SwapFailure(bytes reason);

    function swapNative(
        uint256 amount,
        address to,
        bytes memory data,
        bool sweep,
        address refundAddress
    ) internal {
        (bool ok, bytes memory reason) = to.call{value: amount}(data);
        if (!ok) revert SwapFailure(reason);

        if (sweep) {
            uint256 balance = address(this).balance;
            if (balance > 0) {
                payable(refundAddress).sendValue(balance);
            }
        }
    }

    function swapERC20(
        address token,
        uint256 amount,
        address to,
        bytes memory data,
        bool sweep,
        address refundAddress
    ) internal {
        IERC20(token).approve(to, amount);
        (bool ok, bytes memory reason) = to.call(data);
        if (!ok) revert SwapFailure(reason);
        IERC20(token).approve(to, 0);

        if (sweep) {
            uint256 balance = IERC20(token).balanceOf(address(this));
            if (balance > 0) {
                IERC20(token).safeTransfer(refundAddress, balance);
            }
        }
    }
}

