pragma solidity ^0.8.17;
import "../interfaces/IStargateFactory.sol";
import "./StargatePoolMock.sol";

contract StargateFactoryMock is IStargateFactory {
    //---------------------------------------------------------------------------
    // VARIABLES
    mapping(uint256 => address) public override getPool; // poolId -> PoolInfo
